import { Flex, Stack, Button, Image, Heading, Text } from "@chakra-ui/react"
import { Link } from "react-router-dom";
import analysis from "../../img/analysis.svg";
import { normalGrey } from "../../globals/themes";

const HomePage = () => {
    return (
        <Flex
            flexDir="column"
            as="main"
            align="center"
            justify="center"
            gap="2rem"
            w="100%"
            pt={6}
            pb={6}
            bg={normalGrey}
            color="white"
        >
            <Stack>
                <Flex align="center" justify="space-between">
                    <Heading             
                        bgColor="#ff8177"
                        bgImage="linear-gradient(to top, #ff0844 0%, #ffb199 100%)"
                        bgSize="100%"
                        css={{
                            WebkitBackgroundClip: 'text',
                            WebkitTextFillColor: 'transparent'
                        }}
                        fontSize={["1rem", "2rem", "3rem", "4rem"]}
                    >
                        Resume Rater
                    </Heading>
                    <Button 
                        bgImage="linear-gradient(to top, #f77062 0%, #fe5196 100%)"
                        transition="all 0.35s"
                        _after={{
                            background: "#4837ff",
                            transition: "all 0.35s"
                        }}
                    >
                        <Link to="/rate">
                            Get Started
                        </Link>
                    </Button>
                </Flex>
                <Text    
                    fontSize={["1rem", "1rem", "1.5rem", "2rem"]}
                    fontWeight={700}
                    color={"#fff"}
                >
                    Utilizing natural language processing to make your
                    job searching easier.
                </Text>
            </Stack>
            <Image
                src={analysis} 
                alt={"woman looking at an analysis"}
            />
        </Flex>
    );
}

export default HomePage;
