import { Flex, Stack, Spacer, Button, Image, Heading, Text } from "@chakra-ui/react"
import { Link } from "react-router-dom";
import analysis from "../../img/analysis.svg";

const HomePage = () => {
    return (
        <Flex
            as="main"
            align="center"
            justify="space-between"
            wrap="wrap"
            gap="1rem"
            w="100%"
            mb={4}
            pt={4}
            pb={4}
            pl={8}
            pr={8}
            bg={darkerGrey}
            color="white"
        >
                <Heading             
                    bgColor="#ff8177"
                    bgImage="linear-gradient(to top, #ff0844 0%, #ffb199 100%)"
                    bgSize="100%"
                    css={{
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent'
                    }}
                    fontSize={["1rem", "1rem", "1rem", "1.5rem"]}
                >
                    Resume Rater
                </Heading>
            <Stack
            >
                <Heading             
                    bgColor="#ff8177"
                    bgImage="linear-gradient(to top, #ff0844 0%, #ffb199 100%)"
                    bgSize="100%"
                    css={{
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent'
                    }}
                    fontSize={["1rem", "1rem", "1rem", "1.5rem"]}
                >
                    Resume Rater
                </Heading>
                <Text    
                    fontSize={["1rem", "1rem", "1rem", "1.5rem"]}
                    fontWeight={700}
                    color={"#fff"}
                >
                    Utilizing natural language processing to make your
                    job searching easier.
                </Text>
                <Button w={100}>
                    <Link to="/rate">
                        Get Started
                    </Link>
                </Button>
            </Stack>
            {/* <Image
                flexBasis=""
                src={analysis} 
                alt={"woman looking at an analysis"}
            /> */}
        </Flex>
    );
}

export default HomePage;
