import { Flex, Stack, Button, Image, Heading, Text } from "@chakra-ui/react"
import { Link } from "react-router-dom";
import analysis from "../../img/analysis.svg";

const HomePage = () => {
    return (
        <Flex
            align="center"
            justify="center"
            top={0}
            mb={4}
            pt={4}
            pb={4}
            color="white"
        >
            <Flex w={1024}>
                <Stack
                >
                    <Heading             
                        bgColor="#ff8177"
                        bgImage="linear-gradient(to top, #ff0844 0%, #ffb199 100%)"
                        bgSize="100%"
                        css={{
                            WebkitBackgroundClip: 'text',
                            WebkitTextFillColor: 'transparent'
                        }}
                        fontSize="4rem"
                    >
                        Resume Rater
                    </Heading>
                    <Text    
                        fontSize="2rem"
                        fontWeight={700}
                        color={"#fff"}
                    >
                        Utilizing natural language processing to make your
                        job searching easier.
                    </Text>
                    <Button>
                        <Link to="/rate">
                            Get Started
                        </Link>
                    </Button>
                </Stack>
                <Image 
                    boxSize={800}
                    src={analysis} 
                    alt={"woman looking at an analysis"}
                />
            </Flex>
        </Flex>
    );
}

export default HomePage;
