import { Flex, Stack, Button, Image, Heading, Text } from "@chakra-ui/react"
import { Link } from "react-router-dom";
import analysis from "../../img/analysis.svg";
import { normalGrey } from "../../globals/themes";

const HomePage = () => {
    return (
        <Flex
            flexDir="column"
            as="main"
            align="center"
            justify="center"
            gap="1rem"
            w="100%"
            mt={20}
            bg={normalGrey}
            color="white"
        >
            <Flex
                flexDir="column"
                justify="center"
            >
                <Heading             
                    bgColor="#ff8177"
                    bgImage="linear-gradient(to top, #ff0844 0%, #ffb199 100%)"
                    bgSize="100%"
                    css={{
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent'
                    }}
                    fontSize={["1rem", "2rem", "3rem", "4rem"]}
                >
                    Resume Rater
                </Heading>
                <Text    
                    fontSize={["1rem", "1rem", "1.5rem", "2rem"]}
                    fontWeight={700}
                    color={"#fff"}
                >
                    Utilizing natural language processing to make your
                    job searching easier.
                </Text>
                <Button w={100}>
                    <Link to="/rate">
                        Get Started
                    </Link>
                </Button>
            </Flex>
            <Image
                src={analysis} 
                alt={"woman looking at an analysis"}
            />
        </Flex>
    );
}

export default HomePage;
